import React, { useState, useEffect } from 'react'
import axios from 'axios'

//Ex 2.12: Filter and display countries from API

//High level logics:
// I first need to load the data into a countries variable
// Then, let me try to display the country names
// Then, my input would need to filter the data.
//    -To create my filter,


// Pseudo-code
//


const Countries = (props) => {
  return (
    <>
      {props.countries}
    </>
  )
}

const App = () => {

  const [countries, setCountries] = useState([])
  const [ search, setSearch] = useState('')



  const hook = () => {
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        console.log('promise fulfilled')
        setCountries(response.data)
      })
  }

  useEffect(hook, [])
  console.log(countries)

  //Filter for Countries
  const countryToShow = search === ''
    ? "none"
    : countries.filter(x => x.name.toLowerCase().includes(search))

  const displayedCountries = countryToShow.map(x => <li key={x.name}> {x.name}</li>)

  return (
    <>
    <p>find countries <input value={search} onChange={event => setSearch(event.target.value)}></input> </p>
    <Countries countries={countryToShow} />

    </>
  )
}


export default App
