import './App.css';
import React, { useState } from 'react'
import { createStore } from 'redux'


function App() {
  const [number, setNumber] = useState(0)
  const action = {
    type:'increment'
  }

  const counterReducer = (state = 0, action) => {
    switch (action.type) {
      case 'INCREMENT':
        return state + 1
      case 'DECREMENT':
        return state - 1
      case 'ZERO':
        return 0
      default:
      return state
    }
  }

  const store = createStore(counterReducer)


  return (
    <div style={{margin: "30px"}}>

      {store.getstate()}
      <div>
      <button>+</button>
      <button>-</button>
      <button>zero</button>
      </div>
    </div>
  )
}

export default App;
